import React, {PureComponent} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends PureComponent {


  //constructor
  constructor(){
    super();

    this.styles={
      primaryBtn:{
        border:"1px solid #000",
        width:"25%",
        textAlign:"center",
        padding:"30px"
      }
    }
    this.state = {
      input1: 0,
      input2: 0,
      result: 0
    }
  }

  doAddition(){
     if(this.state.input1 !== 0 && this.state.input2 !== 0){

        this.setState = ({
          ...this.state,
          result: this.state.input1 + this.state.input2
        });
     }else{

        this.setState = ({
          ...this.state,
          result: this.state.input1
        });
     }

     console.log("The state is "+this.state.input1+" and "+this.state.input2+" and res: "+this.state.result);
  }
  getInput(a){
     //console.log(a);
     if(this.state.input1 === 0 ){
        this.setState({
          ...this.state,  
          input1: a

        });
     }else{
        this.setState({
            ...this.state,
            input2: a
        });
     }

     console.log("The state is "+this.state.input1+" and "+this.state.input2+" and res: "+this.state.result);
  }
  render(){
   return (
    <div className="container">
      <div className="display">
         <h1>0</h1>
      </div>
      <div className="keypad">
      <table style={{width:"100%"}}>
       <tbody>  
        <tr>
          <td onClick={this.getInput.bind(this,1)} style={this.styles.primaryBtn}>1</td>
          <td onClick={this.getInput.bind(this,2)} style={this.styles.primaryBtn}>2</td>
          <td onClick={this.getInput.bind(this,3)} style={this.styles.primaryBtn}>3</td>
          <td onClick={this.doAddition.bind(this)} style={this.styles.primaryBtn}>+</td>
        </tr>
        <tr>
          <td style={this.styles.primaryBtn}>4</td>
          <td style={this.styles.primaryBtn}>5</td>
          <td style={this.styles.primaryBtn}>6</td>
          <td style={this.styles.primaryBtn}>-</td>
        </tr>
        <tr>
          <td style={this.styles.primaryBtn}>7</td>
          <td style={this.styles.primaryBtn}>8</td>
          <td style={this.styles.primaryBtn}>9</td>
          <td style={this.styles.primaryBtn}>*</td>
        </tr>
        <tr>
          <td style={this.styles.primaryBtn}>Clear</td>
          <td style={this.styles.primaryBtn}>0</td>
          <td style={this.styles.primaryBtn}>=</td>
          <td style={this.styles.primaryBtn}>/</td>
        </tr>
       </tbody>
      </table>
      </div>
    </div>
   );
  }
}

export default App;
