import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Display from './Components/Display';
import Button from './Components/Button';

class App extends Component {

  //constructor
  constructor(props){
    super(props);

    this.styles={
      primaryBtn:{
        border:"1px solid #000",
        width:"25%",
        textAlign:"center",
        padding:"30px"
      }
    }

    this.state ={
      input: "",
      prevNum: 0,
      prevOperand: "+"
    }

  }

  getInput = (a) => {
    this.setState({ input: this.state.input+a})
  }

  doProcess = (op) => {
    let input1 = parseInt(this.state.input);

    switch(this.state.prevOperand ){
      case "+":
          this.setState({ prevNum: this.state.prevNum + input1 , input:""});
          break;

      case "-":
          this.setState({ prevNum: this.state.prevNum - input1 , input:""});
          break;

      case "*":
          this.setState({ prevNum: this.state.prevNum * input1 , input:""});
          break;

      case "/":
          this.setState({ prevNum: this.state.prevNum / input1 , input:""});
          break;

      case "=":
          this.setState({ prevNum: this.state.prevNum });
          break;

      default:
          this.setState({input: "ERROR"});
    }

    this.setState({prevOperand: op});
  }

  doClearOp = () => {
    this.setState({ prevNum: 0 , input:""});
  }

  render(){
    console.log("[RENDER] prevNum: "+this.state.prevNum+" and input: "+this.state.input);
   return (
    <div className="container">

      <Display>    
          {this.state.input === "" ? this.state.prevNum.toString() : this.state.input}
      </Display>

      <div className="keypad"> 
        <div className="BtnRow">
          <Button onClick={() => this.getInput("1")}>1</Button>
          <Button onClick={() => this.getInput("2")}>2</Button>
          <Button onClick={() => this.getInput("3")}>3</Button>
          <Button onClick={() => this.doProcess("+")}>+</Button>
        </div>
        <div className="BtnRow">
          <Button onClick={() => this.getInput("4")} >4</Button>
          <Button onClick={() => this.getInput("5")} >5</Button>
          <Button onClick={() => this.getInput("6")} >6</Button>
          <Button onClick={() => this.doProcess("-")} >-</Button>
        </div>
        <div className="BtnRow">
          <Button onClick={() => this.getInput("7")} >7</Button>
          <Button onClick={() => this.getInput("8")}>8</Button>
          <Button onClick={() => this.getInput("9")} >9</Button>
          <Button onClick={() => this.doProcess("*")}>*</Button>
        </div>
        <div className="BtnRow">
          <Button onClick={() => this.doClearOp()} >Clear</Button>
          <Button onClick={() => this.getInput("0")}>0</Button>
          <Button onClick={() => this.doProcess("=")}>=</Button>
          <Button onClick={() => this.doProcess("/")}>/</Button>
        </div>
      </div>
    </div>
   );
  }
}

export default App;
