import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {


  //constructor
  constructor(props){
    super(props);

    this.input1=0;
    this.input2=0;
    this.operand='';
    this.fetchedInput = '';

    this.storeEvalString = "";

    this.styles={
      primaryBtn:{
        border:"1px solid #000",
        width:"25%",
        textAlign:"center",
        padding:"30px"
      }
    }

    this.state ={
      result: 0
    }

  }


