import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {


  //constructor
  constructor(){
    super();

    this.input1=0;
    this.input2=0;

    this.styles={
      primaryBtn:{
        border:"1px solid #000",
        width:"25%",
        textAlign:"center",
        padding:"30px"
      }
    }

    this.state = {
      result: ""
    }
  }

  doAddition(){
     let val;
     if(this.input1 !== 0 && this.input2 !== 0){
        val = this.input1 + this.input2;
     }else{
        val = this.input1;
     }

     this.setState = ({
        ...this.state,
        result: val
     });

     console.log("The state is "+this.input1+" and "+this.input2+" and res: "+this.state.result);
  }
  getInput(a){
     //console.log(a);
     if(this.input1 === 0 ){
        this.input1 = a;
     }else{
        this.input2 = a
     }

     console.log("The state is "+this.input1+" and "+this.input2+" and res: "+this.state.result);
     return;
  }
  render(){
   return (
    <div className="container">
      <div className="display">
         <h1>0</h1>
      </div>
      <div className="keypad">
      <table style={{width:"100%"}}>
       <tbody>  
        <tr>
          <td onClick={() => this.getInput(1)} style={this.styles.primaryBtn}>1</td>
          <td onClick={() => this.getInput(2)} style={this.styles.primaryBtn}>2</td>
          <td onClick={() => this.getInput(3)} style={this.styles.primaryBtn}>3</td>
          <td onClick={() => this.doAddition()} style={this.styles.primaryBtn}>+</td>
        </tr>
        <tr>
          <td style={this.styles.primaryBtn}>4</td>
          <td style={this.styles.primaryBtn}>5</td>
          <td style={this.styles.primaryBtn}>6</td>
          <td style={this.styles.primaryBtn}>-</td>
        </tr>
        <tr>
          <td style={this.styles.primaryBtn}>7</td>
          <td style={this.styles.primaryBtn}>8</td>
          <td style={this.styles.primaryBtn}>9</td>
          <td style={this.styles.primaryBtn}>*</td>
        </tr>
        <tr>
          <td style={this.styles.primaryBtn}>Clear</td>
          <td style={this.styles.primaryBtn}>0</td>
          <td style={this.styles.primaryBtn}>=</td>
          <td style={this.styles.primaryBtn}>/</td>
        </tr>
       </tbody>
      </table>
      </div>
    </div>
   );
  }
}

export default App;
