import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Display from './Components/Display';

class App extends Component {


  //constructor
  constructor(props){
    super(props);

    this.styles={
      primaryBtn:{
        border:"1px solid #000",
        width:"25%",
        textAlign:"center",
        padding:"30px"
      }
    }

    this.state ={
      input: "",
      result: 0,
    }

  }

  getInput = (a) => {
    this.setState({ input: this.state.input+a, res: this.state.input+a})
  }

  add = () => {
    if(this.state.result === 0){
      this.setState({
        result: parseInt(this.state.input),
        input: ""
      })
    }else{
      const res = parseInt(this.state.input) + this.state.result;
      this.setState({
        result: res,
        input: ""
      })
    }
  }

  sub = () => {
    if(this.state.result === 0){
      this.setState({
        result: parseInt(this.state.input),
        input: ""
      })
    }else{
      const res = parseInt(this.state.input) - this.state.result;
      this.setState({
        result: res,
        input: ""
      })
    }
  }

  mul = () => {
    if(this.state.result === 0){
      this.setState({
        result: parseInt(this.state.input),
        input: ""
      })
    }else{
      const res = parseInt(this.state.input) * this.state.result;
      this.setState({
        result: res,
        input: ""
      })
    }
  }

  divide = () => {
    if(this.state.result === 0){
      this.setState({
        result: parseInt(this.state.input),
        input: ""
      })
    }else{
      const res = parseInt(this.state.input) / this.state.result;
      this.setState({
        result: res,
        input: ""
      })
    }
  }

  render(){
    console.log("[RENDER] prevNum: "+this.state.prevNum+" and nextNum: "+this.state.nextNum);
   return (
    <div className="container">
      <Display>
          {this.state.result}
      </Display>

      <div className="keypad">
      <table style={{width:"100%"}}>
       <tbody>  
        <tr>
          <td onClick={() => this.getInput("1")} style={this.styles.primaryBtn}>1</td>
          <td onClick={() => this.getInput("2")} style={this.styles.primaryBtn}>2</td>
          <td onClick={() => this.getInput("3")} style={this.styles.primaryBtn}>3</td>
          <td onClick={() => this.add()} style={this.styles.primaryBtn}>+</td>
        </tr>
        <tr>
          <td onClick={() => this.getInput("4")} style={this.styles.primaryBtn}>4</td>
          <td onClick={() => this.getInput("5")} style={this.styles.primaryBtn}>5</td>
          <td onClick={() => this.getInput("6")} style={this.styles.primaryBtn}>6</td>
          <td onClick={() => this.sub()} style={this.styles.primaryBtn}>-</td>
        </tr>
        <tr>
          <td onClick={() => this.getInput("7")} style={this.styles.primaryBtn}>7</td>
          <td onClick={() => this.getInput("8")} style={this.styles.primaryBtn}>8</td>
          <td onClick={() => this.getInput("9")} style={this.styles.primaryBtn}>9</td>
          <td onClick={() => this.mul()} style={this.styles.primaryBtn}>*</td>
        </tr>
        <tr>
          <td onClick={() => this.getInput("cl")} style={this.styles.primaryBtn}>Clear</td>
          <td onClick={() => this.getInput("0")} style={this.styles.primaryBtn}>0</td>
          <td onClick={() => this.doCalculation("0")} style={this.styles.primaryBtn}>=</td>
          <td onClick={() => this.divide()} style={this.styles.primaryBtn}>/</td>
        </tr>
       </tbody>
      </table>
      </div>
    </div>
   );
  }
}

export default App;
